import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode } from "react"
import HTMLFlipBook from "react-pageflip"

// Define the interface for the component props
interface IProps {
  style: React.CSSProperties
  startPage: number
  size: string
  minWidth: number // Change type to number
  // Add other required properties here
}

/**
 * This is a React-based component template. The `render()` function is called
 * automatically when your component should be re-rendered.
 */
class MyComponent extends StreamlitComponentBase {
  public render = (): ReactNode => {
    // Arguments that are passed to the plugin in Python are accessible
    // via `this.props.args`. Here, we access the "name" arg.

    return (
      <HTMLFlipBook
        className="book"
        width={600}
        height={600}
        style={{}} // Add the required style property
        startPage={1} // Add the required startPage property
        size="stretch" // Add the required size property
        minWidth={320} // Change to number value
      >
        <div className="demoPage">
          <img src={image1} alt="Page 1" />
          <div className="pageText">Text for Page 1</div>
        </div>
        <div className="demoPage">
          <img src={image1} alt="Page 2" />
          <div className="pageText">Text for Page 2</div>
        </div>
        <div className="demoPage">
          <img src={image1} alt="Page 3" />
          <div className="pageText">Text for Page 3</div>
        </div>
        <div className="demoPage">
          <img src={image1} alt="Page 4" />
          <div className="pageText">Text for Page 4</div>
        </div>
      </HTMLFlipBook>
    )
  }
}

// "withStreamlitConnection" is a wrapper function. It bootstraps the
// connection between your component and the Streamlit app, and handles
// passing arguments from Python -> Component.
//
// You don't need to edit withStreamlitConnection (but you're welcome to!).
export default withStreamlitConnection(MyComponent)
